import Path, { PathProps } from '../Path';
import * as roundRectHelper from '../helper/roundRect';
import {subPixelOptimizeRect} from '../helper/subPixelOptimize';

export class RectShape {
    // å·¦ä¸Šã€å³ä¸Šã€å³ä¸‹ã€å·¦ä¸‹è§’çš„åŠå¾„ä¾æ¬¡ä¸ºr1ã€r2ã€r3ã€r4
    // rç¼©å†™ä¸º1         ç›¸å½“äº [1, 1, 1, 1]
    // rç¼©å†™ä¸º[1]       ç›¸å½“äº [1, 1, 1, 1]
    // rç¼©å†™ä¸º[1, 2]    ç›¸å½“äº [1, 2, 1, 2]
    // rç¼©å†™ä¸º[1, 2, 3] ç›¸å½“äº [1, 2, 3, 2]
    r?: number | number[]

    x = 0
    y = 0
    width = 0
    height = 0
}

export interface RectProps extends PathProps {
    shape?: Partial<RectShape>
}
// Avoid create repeatly.
const subPixelOptimizeOutputShape = {};

class Rect extends Path<RectProps> {

    shape: RectShape

    constructor(opts?: RectProps) {
        super(opts);
    }

    getDefaultShape() {
        return new RectShape();
    }

    buildPath(ctx: CanvasRenderingContext2D, shape: RectShape) {
        let x: number;
        let y: number;
        let width: number;
        let height: number;

        if (this.subPixelOptimize) {
            const optimizedShape = subPixelOptimizeRect(subPixelOptimizeOutputShape, shape, this.style);
            x = optimizedShape.x;
            y = optimizedShape.y;
            width = optimizedShape.width;
            height = optimizedShape.height;
            optimizedShape.r = shape.r;
            shape = optimizedShape;
        }
        else {
            x = shape.x;
            y = shape.y;
            width = shape.width;
            height = shape.height;
        }

        if (!shape.r) {
            ctx.rect(x, y, width, height);
        }
        else {
            roundRectHelper.buildPath(ctx, shape);
        }
    }

    isZeroArea() {
        return !this.shape.width || !this.shape.height;
    }
}

Rect.prototype.type = 'rect';

export default Rect;